glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
#if USE_TINT
  in vec4 a_color0;
#endif
in vec2 a_uv0;
out vec2 v_uv0;
out vec4 v_light;
#if USE_TINT
  out vec4 v_dark;
#endif
void main() {
  mat4 mvp;
  #if CC_USE_MODEL
    mvp = cc_matViewProj * cc_matWorld;
  #else
    mvp = cc_matViewProj;
  #endif
  v_uv0 = a_uv0;
  v_light = a_color;
  #if USE_TINT
    v_dark = a_color0;
  #endif
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
uniform sampler2D texture;
in vec2 v_uv0;
in vec4 v_light;
#if USE_TINT
  in vec4 v_dark;
#endif
uniform Constant {
  float uFlash;
};
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
const vec3 FLASH = vec3(1.0);
const vec3 VENO = vec3(0.2, 0.96, 0.2);
const vec3 FROST = vec3(0.3, 0.2, 0.75);
const vec3 THUNDER = vec3(0.8, 0.95, 0.1);
const vec3 bw = vec3(0.3, 0.6, 0.1);
vec3 Blackwhite(in vec3 o) {
  vec3 final = vec3(dot(o, bw));
  return final;
}
vec3 Inverted(in vec3 o) {
  vec3 final = vec3(1.0) - o;
  return final;
}
void main() {
  vec4 texColor = vec4(1.0);
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    texColor.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    texColor.a *= texture_tmp.a;
  #else
    texColor *= texture_tmp;
  #endif
  vec4 finalColor;
  #if USE_TINT
    finalColor.a = v_light.a * texColor.a;
    finalColor.rgb = ((texColor.a - 1.0) * v_dark.a + 1.0 - texColor.rgb) * v_dark.rgb + texColor.rgb * v_light.rgb;
  #else
    if (uFlash > 0.7) {
      vec4 o = texture(texture, v_uv0);
      vec3 BW = Blackwhite(o.rgb);
      vec3 IV = Inverted(BW);
      o.rgb = mix(FLASH, o.rgb, v_light.r);
      finalColor = o;
    }else {
      finalColor = texColor * v_light;
    }
  #endif
  ALPHA_TEST(finalColor);
  gl_FragColor = finalColor;
}

